	for(lli i=l;i<=r;i++)
	{
		v.push_back(i);
	}
	lli count1=0;
	for(size_t i=0;i<v.size()-1;i++)
	{
		for(size_t j=i;j<v.size();j++)
		{
			lli a,b;
			a=v[i];
			b=v[j];
			lli g=gcd(a,b);
