#include <bits/stdc++.h>
using namespace std;

#define INF 100000000
#define MX 500005
#define MM 200005
int P[MM], val[MX], root[MX];
bool chk[300005], vis[MM];
vector<int> adj[MM], Arr, S[MX];

int N, M, Q;
int L[MM], R[MM], st[MM], en[MM], pos[MM];
bool noRoot[MX], op[MX];

struct Edge {
	int s, e;
	void in() { cin >> s >> e; s --, e --; }
} E[300005];

void dfs(int u, int n) {
	root[u] = n;
	vis[u] = 1;
	S[n].push_back(u);
	for(auto i : adj[u]) if(chk[i] == false) {
		int v = E[i].s + E[i].e - u;
		if(!vis[v]) dfs(v, n);
	}
}

int find_root(int u) {
	if(root[u] == u) return u;
	return root[u] = find_root(root[u]);
}

void dfs1(int u) {
	if(L[u - N] == 0 && R[u - N] == 0) {
		st[u - N] = Arr.size();
		for(auto v : S[u]) Arr.push_back(v);
		en[u - N] = Arr.size() - 1;
	}
	else {
		dfs1(L[u - N]), dfs1(R[u - N]);
		st[u - N] = min(st[L[u - N] - N], st[R[u - N] - N]);
		en[u - N] = max(en[L[u - N] - N], en[R[u - N] - N]);
	}
}

struct WinnerTree {
	int v[MX * 4];
	int n;
	void build(int an) {
		int i;
		for (n = 1; n < an; n *= 2);
		for (i = 0; i < an; i++) v[n + i] = P[Arr[i]];
		for (i = an; i < n; i++) v[n + i] = P[Arr[0]];
		for (i = n - 1; i > 0; i--) v[i] = max(v[i * 2], v[i * 2 + 1]);
	}
	void up(int i, int val) {
		i += n;
		v[i] = val;
		for (i /= 2; i; i /= 2) v[i] = max(v[i * 2], v[i * 2 + 1]);
	}
	int get(int st, int en) {
		int rlt = 0;
		st += n, en += n;
		while (st < en) {
			if (st & 1) rlt = max(rlt, v[st]);
			if (en & 1) rlt = max(rlt, v[en - 1]);
			st = (st + 1) / 2;
			en /= 2;
		}
		return rlt;
	}
} WT;

int main() {
//	freopen("in.txt", "r", stdin);

	ios::sync_with_stdio(0);
	cin.tie(0); cout.tie(0);

	cin >> N >> M >> Q;
	for(int i = 0; i < N; i ++) cin >> P[i];
	for(int i = 0; i < M; i ++) {
		E[i].in();
		adj[E[i].s].push_back(i);
		adj[E[i].e].push_back(i);
	}
	for(int i = 0; i < Q; i ++) {
		int tp;
		cin >> tp >> val[i]; val[i] --;
		op[i] = tp - 1;
		if(op[i]) chk[val[i]] = 1;
	}
	for(int i = 0; i < MX; i ++) root[i] = i;
	int n = N;
	for(int i = 0; i < N; i ++) if(!vis[i]) {
		n ++;
		dfs(i, n);
	}
	for(int i = Q - 1; i >= 0; i --) if(op[i] == 1) {
		for(int j = i + 1; op[j] == 0 && j < Q; j ++) {
			int u = val[j];
			val[j] = find_root(u);
		}
		int u = E[val[i]].s, v = E[val[i]].e;
		int rtu = find_root(u), rtv = find_root(v);
		if(rtu == rtv) continue;
		n ++;
		L[n - N] = rtu, R[n - N] = rtv;
		root[rtu] = n, root[rtv] = n;
		noRoot[rtu] = 1, noRoot[rtv] = 1;
	}
	for(int i = 0; i < Q && !op[i]; i ++) {
		int u = val[i];
		val[i] = find_root(u);
	} 

	for(int i = N + 1; i <= n; i ++) if(!noRoot[i]) dfs1(i);
	assert(Arr.size() == N);
	for(int i = 0; i < N; i ++) pos[P[Arr[i]]] = i;

	WT.build(N);
	for(int i = 0; i < Q; i ++) if(op[i] == 0) {
		int s = st[val[i] - N], e = en[val[i] - N];
		int ans = WT.get(s, e + 1);
		cout << ans << endl;
		if(ans == 0) continue;
		int p = pos[ans];
		WT.up(p, 0);
	}

	return 0;
}

#include <iostream>
