using namespace std;
#define x first
#define y second
#define pb push_back
#define int long long 
typedef long double ld;
const int MAXN = 1e5 + 7;
int delt[MAXN];
int a[MAXN];
int t[4 * MAXN];
int dp[MAXN];
int get(int v, int tl, int tr, int l, int r) {
	if (tr < l || r < tl)
		return 0;
	else if (l <= tl && tr <= r)
		return t[v];
	else {
		int tm = (tl + tr) / 2;
		return max(get(2 * v, tl, tm, l, r), get(2 * v + 1, tm + 1, tr, l, r));
	}
}
int upd(int v, int tl, int tr, int p, int vl) {
	if (tl == tr) 
		t[v] = max(t[v], vl);
	else {
		int tm = (tl + tr) / 2;
		if (p <= tm)
			upd(2 * v, tl, tm, p, vl);
		else
			upd(2 * v + 1, tm + 1, tr, p, vl);
		t[v] = max(t[2 * v], t[2 * v + 1]);
	}
}
void solve() {
	int n, m;
	cin >> n >> m;
	for (int i = 0; i < n; ++i) {
		int a, b;
		cin >> a >> b;
		--a;
		++delt[a];
		--delt[b];
	}
	int sum = 0, ans = 0;
	for (int i = 0; i < m; ++i) {
		sum += delt[i];
		a[i] = sum;
		dp[i] = 1;
		if (a[i]) 
			dp[i] = get(1, 0, MAXN - 1, 0, a[i]) + 1;
		upd(1, 0, MAXN - 1, a[i], dp[i]);
		ans = max(ans, dp[i]);
	}
	cout << ans;
}
signed main() {
	freopen("A.in", "r", stdin);
	freopen("A.out", "w", stdout);
	ios_base::sync_with_stdio(false);

	solve();

	return 0;
}

#include <bits/stdc++.h>

using namespace std;

#define forn(i, n) for (int i = 0; i < (n); i++)
#define pb push_back
#define sz(a) int(a.size())
#define x first
#define y second

typedef long long ll;
typedef long double ld;

const int N = 1e6 + 9;
const char C = 'a';

int dp1[N], dp2[N], t[N*4], a[N], op[N], cl[N], mx1[N], mx2[N];

void precount(int n) {
        int cur = 0;
        forn(i, n) {
                cur += op[i];
                a[i] = cur;
                cur -= cl[i];
        }
}

int range_max(int v, int t_l, int t_r, int l, int r) {
        if (l > r)
                return 0;
        if (l == t_l && r == t_r) 
                return t[v];
        int t_m = (t_l + t_r)/2;
        return max(range_max(v*2, t_l, t_m, l, min(r, t_m)),
                range_max(v*2 + 1, t_m + 1, t_r, max(t_m + 1, l), r));
}

void update(int v, int l, int r, int pos, int val) {
        if (l == r) {
                t[v] = max(t[v], val);
                return;
        }
        int m = (l + r)/2;
        if (pos <= m)
                update(v*2, l, m, pos, val);
        else
                update(v*2 + 1, m + 1, r, pos, val);
        t[v] = max(t[v*2], t[v*2 + 1]);
}

void count_dp(int dp[], int n) {
        fill(t, t + N*4, 0);
        forn(i, n) {
                dp[i] = range_max(1, 0, n - 1, 0, a[i]) + 1;
                update(1, 0, n - 1, a[i], dp[i]);
        }
}

int main() {
	ios::sync_with_stdio(0);
        int n, m, l, r, ans = 0;
        cin >> m >> n;
        forn(i, m) {
                cin >> l >> r;
                l--;
                r--;
                op[l]++;
                cl[r]++;
        }
        precount(n);
        count_dp(dp1, n);
        reverse(a, a + n);
        count_dp(dp2, n);
        mx1[0] = dp1[0];
        mx2[0] = dp2[0];
        for (int i = 1; i < n; i++) {
                mx1[i] = mx1[i - 1];
                mx2[i] = mx2[i - 1];
                mx1[i] = max(mx1[i], dp1[i]);
                mx2[i] = max(mx2[i], dp2[i]);
        }
        reverse(mx2, mx2 + n);
        forn(i, n) {
                ans = max(ans, mx1[i] + mx2[i] - 1);
        }
        cout << ans;
	return 0;
}

