#define fo(a,b,c) for(int a=b;a>=c;a--)
#define long long long
#define pii pair<int,int>
#define mp make_pair

const int N = 2e6+9;

struct String{
  string s;
  int pos;

  bool operator< (String b) const{
    if (s.length() < b.s.length())
      return true;
    if (s.length() > b.s.length())
      return false;
    return (s < b.s) || (s == b.s && pos > b.pos);
  }
};

struct Node{
  vector<int> pos;
  Node *p[2];

  Node () {
    p[0] = p[1] = NULL;
    pos.clear();
  }
} trie;

struct Position{
  int cost, pos;
  Node *pos_trie;
  Position (){}
  Position (int cost_, int pos_, Node *pos_trie_){
    cost = cost_, pos = pos_, pos_trie = pos_trie_;
  }
};

struct Query{
  int l, r, pos;
  string s;

  bool operator< (Query b) const{
    return (s.length() > b.s.length()) || (s.length() == b.s.length() && pos < b.pos);
  }
};

struct IT{
  int c[N];
  pii mx[N*4];

  IT(){
    fi(i,0,N-1)
      c[i] = 0;
    fi(i,0,4*N-1)
      mx[i] = mp(0,0);
  }

  void Build(int k, int l, int r){
    if (l == r){
      mx[k] = mp(c[l],l);
      return;
    }

    int mid = l+r>>1;
    Build(k<<1, l, mid);
    Build(k<<1|1, mid+1, r);
    if (mx[k<<1].first > mx[k<<1|1].first)
      mx[k] = mx[k<<1];
    else
      mx[k] = mx[k<<1|1];
  }

  void Update(int k, int l, int r, int x){
    if (r < x || x < l) return;

    if (l == r){
      mx[k] = mp(c[l],l);
      return;
    }

    int mid = l+r>>1;
    Update(k<<1, l, mid, x);
    Update(k<<1|1, mid+1, r, x);
    if (mx[k<<1].first > mx[k<<1|1].first)
      mx[k] = mx[k<<1];
    else
      mx[k] = mx[k<<1|1];
  }

  pii Get(int k, int l, int r, int x, int y){
    if (r < x || y < l) return mp(0,0);

    if (x <= l && r <= y)
      return mx[k];

    int mid = l+r>>1;
    pii res1 = Get(k<<1, l, mid, x, y);
    pii res2 = Get(k<<1|1, mid+1, r, x, y);
    if (res1.first > res2.first)
      return res1;
    else
      return res2;
  }
} it;

