void Init(){
  Node *p = &trie;
  pos_zero_trie[1000001] = &trie;
  fo(i,1000000,s[1].s.length()){
    p->p[0] = new Node();
    p = p->p[0];
    pos_zero_trie[i] = p;
  }
}

void Update_List(int x){
  int len = s[x].s.length();
  Node *p = pos_zero_trie[len+1];
  fi(l,0,len-1){
    int i = len-l;
    int ch = s[x].s[l]-'0';
    if (p->p[ch] == NULL)
      p->p[ch] = new Node();
    p = p->p[ch];
    list_[i].push_back(Position(x,s[x].pos,p));
  }
}

void Add(int x){
  int len = str[x].length();
  Node *p = pos_zero_trie[len+1];
  fi(i,0,len-1){
    int ch = str[x][i]-'0';
    p = p->p[ch];
    p->pos.push_back(x);
  }
}

int Find(int l_, int r_, Node *p){
  if (p->pos.empty()) return -1;
  int l = -1, r = p->pos.size();
  while (l+1<r){
    int mid = l+r>>1;
    if (p->pos[mid] < l_)
      l = mid;
    else
      r = mid;
  }

  if (r == p->pos.size() || p->pos[r] > r_)
    return -1;
  else
    return p->pos[r];
}

int Get(int l, int r, string s, Node *p){
  fi(i,0,s.length()-1){
    int ch = s[i]-'0'^1;
    if (p->p[ch] == NULL || Find(l, r, p->p[ch]) == -1)
      ch ^= 1;
    p = p->p[ch];
  }
  return Find(l, r, p);
}

int Get_(int l, int r, string s, Node *p){
  while (!cango.empty()) cango.pop_back();

  Node *tmpp = p;
  int len = 0;
  fi(i,1,s.length()){
    if (tmpp->p[1] != NULL && Find(l, r, tmpp->p[1]) != -1 && len < s.length())
      cango.push_back(len);
    if (tmpp->p[0] == NULL)
      break;
    tmpp = tmpp->p[0]; len++;
  }

  len = 0;
  fi(i,0,s.length()-1){
    int ch = s[i]-'0'^1;
    if (ch == 0 && len != -1){
      while (!cango.empty() && len >= cango.front())
        cango.pop_front();
      if (cango.empty())
        ch = 1, len = -1;
    }

    if (ch == 1 || len == -1)
      if (p->p[ch]
           == NULL || Find(l, r, p->p[ch]) == -1)
        ch ^= 1;

    if (ch) len = -1;
    if (len != -1)
      len++;
    p = p->p[ch];
  }

  return Find(l,r,p);
}

