	while (idx >= 0) {
		int nxt = idx - 1;
		while (nxt >= 0 && arr[nxt] == arr[idx]) nxt --;

		qs.push(make_pair(arr[idx], idx - nxt));
		idx = nxt;
	}

	idx = t;
	while (idx < n) {
		int nxt = t + 1;
		while (nxt < n && arr[nxt] == arr[idx]) nxt ++;

		qt.push(make_pair(arr[idx], nxt - idx));
		idx = nxt;
	}

	int res = 0;
	while (!qs.empty() && !qs.empty()) {
		pii ps = qs.front(), pt = qt.front();
		qs.pop();
		qt.pop();

		if (ps.first != pt.first) break;

		if (ps.second + pt.second < 3) break;

		res += ps.second + pt.second;
	}

	return res;
}

