void countNyms() {
	nym[0] = nym[1] = nym[2] = 0;
	for (int i = 3; i < maxp; i++) {
		int ns[2] = {nym[i / 3], nym[i - i / 3]};
		sort(ns, ns + 2);
		if (ns[0])
			nym[i] = 0;
		else {
			nym[i] = ns[1] == 1 ? 2 : 1; 
		}
	}

