const int MOD = (int)1e9 + 7;
void add(int &x, int toadd) {
	if ((x += toadd) >= MOD)
		x -= MOD;
}

int mult(int a, int b) {
	return (ll)a * b % MOD;
}

int mpower(int x, int pw) {
	int res = 1;
	for (; pw; pw >>= 1) {
		if (pw & 1)
			res = mult(res, x);
		x = mult(x, x);
	}
	return res;
}

int inv(int x) {
	return mpower(x, MOD - 2);
}

const int maxn = (int)1e3;
int C[maxn + 1][maxn + 1];

int main() {     
	#ifdef DEBUG
		freopen(TASKNAME".in", "r", stdin);
		freopen(TASKNAME".out", "w", stdout);
	#endif

	countNyms();
	{
		memset(C, 0, sizeof(C));
		for (int n = 0; n <= maxn; n++) {
			C[n][0] = C[n][n] = 1;
			for (int k = 1; k < n; k++)
				C[n][k] = C[n - 1][k - 1], add(C[n][k], C[n - 1][k]);
		}
	}

	int n, p;
	while (scanf("%d%d", &n, &p) >= 1) {
		assert(p <= maxp);

		int cnt[3] = {0, 0, 0};
		for (int i = 1; i < p; i++)
			add(cnt[nym[i]], p - i);
		eprintf("%d %d %d\n", cnt[0], cnt[1], cnt[2]);

		int ans = cnt[0];
		add(ans, cnt[1]);
		add(ans, cnt[2]);
		ans = mpower(ans, n);

// 		eprintf("%d\n", ans);
		for (int a = 0; a <= n; a += 2)
			for (int b = 0; b <= n - a; b += 2) {
				int c = n - a - b;
// 				eprintf("%d %d %d\n", c, a, b);
				int tmp = mult(C[n][a], C[n - a][b]);
				tmp = mult(tmp, mpower(cnt[0], c));
				tmp = mult(tmp, mpower(cnt[1], a));
				tmp = mult(tmp, mpower(cnt[2], b));
// 				eprintf("tmp = %d\n", tmp);
				add(ans, MOD - tmp);
			}
		printf("%d\n", ans);
// 		break;
	}	
	return 0;
}

