int dp[11][2000];
int num[11];
int c[11];
int d[11];

int main() {
    int n, m, c0, d0;
    cin >> m >> n >> c0 >> d0;
    for (int i = 1; i <= n; i++) {
        int a, b;
        cin >> a >> b;
        num[i] = a / b;
        cin >> c[i] >> d[i];
    }

    memset(dp, -1, sizeof(dp));

    dp[0][0] = 0;
    for (int j = 1; j <= c0 && j * c0 <= m; j++) {
        dp[0][j * c0] = j * d0;
    }

    for (int i = 0; i < n; i++) {
        for (int j = 0; j <= m; j++) {
            if (dp[i][j] != -1) {
                for (int t = 0; t <= num[i + 1]; t++) {
                    int ww = j + t * c[i + 1];
                    if (ww > m) {
                        break;
                    }
                    dp[i + 1][ww] = max(dp[i + 1][ww], dp[i][j] + t * d[i + 1]);
                }
            }
        }
    }

    int ans = 0;
    for (int i = 0; i <= m; i++) {
        ans = max(ans, dp[n][i]);
    }
    cout << ans << endl;
    return 0;
}

