using namespace std;

struct LuckyNumber {
	int number;
	int digits;
};

queue<LuckyNumber> q;

int BFS(int maxDigits) {
	int counter = 0;
	LuckyNumber tmp;

	tmp.number = 7;
	tmp.digits = 1;
	q.push(tmp);

	tmp.number = 8;
	tmp.digits = 1;
	q.push(tmp);

	while(!q.empty()) {
		int size = q.size();

		while(size--) {
			LuckyNumber fr = q.front();
			q.pop();

			if(fr.digits > maxDigits)
				return counter;

			tmp = fr;
			tmp.number *= 10;
			tmp.number += 7;
			tmp.digits++;
			q.push(tmp);

			tmp = fr;
			tmp.number *= 10;
			tmp.number += 8;
			tmp.digits++;
			q.push(tmp);

			counter++;
		}
	}
}

int main() {
	int n;
	cin >> n;

	cout << BFS(n) << endl;

    return 0;
}

