#include <iostream>
#include <cstring>
#define MAXI 8
#define MAXL 9
#define MOD 1000000007

using namespace std;

long long f[MAXI][MAXL][2],c[MAXL];
int n,length,an[MAXL];

inline void update(long long &i, const long long j)
{
    i=(i+j)%MOD;
}

inline int isfs(const int k)
{
    return (k==4||k==7);
}

inline void init()
{
    memset(f,0,sizeof(f));
    f[0][0][1]=1;

    for(int i=0;i<length;i++)
    {
        for(int j=0;j<=i;j++)
        {
            int k;
            // 
            for(k=0;k<an[i];k++)
            {
                update(f[i+1][j+isfs(k)][1], f[i][j][1]+f[i][j][0]);
            } 

            //
            update(f[i+1][j+isfs(k)][1],f[i][j][1]);
            update(f[i+1][j+isfs(k)][0],f[i][j][0]);
            k++; 

            // 
            for(;k<=9;k++)
            {
                update(f[i+1][j+isfs(k)][0], f[i][j][1]+f[i][j][0]);
            }
        }
    }

    for(int i=0;i<=length;i++)
        c[i]=f[length][i][1];
    c[0]--;
}

inline long long dfs(int depth,int left47)
{
    if(depth==MAXI)
        return 1;

    long long ret=0;
    for(int c47=0;c47<=left47;c47++)
    {
        update(ret,(c[c47]--)*dfs(depth+1,left47-c47));
        c[c47]++;
    }
    return ret;
}

inline void solve()
{
    long long ret=0;
    for(int i=1;i<=length;i++)
    {
        ret=(ret+dfs(2,i-1)*c[i])%MOD;
    }
    cout<<ret<<endl;
}

int main()
{
    cin>>n;
    for(int k=n,i=0;k;an[i++]=k%10,k/=10,length++);

    init();
    solve();
    while(1);

    return 0;    
}

