int ans(ll n) {
  if (dp.count(n)) return dp[n];
  int ret = 0;
  for (ll x = max(0, n / 2 - 2); x <= n / 2 + 2; x++) {
    if (x >= 0 && x <= n && abs(x - (n - x)) <= 1) {
      ret = max(ret, max(ans(x), ans(n - x)) + 1);
    }
  }
  return dp[n] = ret;
}

