inline void taoDL(int k, char c)
{
	switch (c)
	{
	case 'U':
		A[k][1] = A[k - 1][1];
		A[k][2] = A[k - 1][2];
		A[k][3] = A[k - 1][3];
		A[k][0] = A[k - 1][0] + 1;
		break;
	case 'D':
		A[k][0] = A[k - 1][0];
		A[k][2] = A[k - 1][2];
		A[k][3] = A[k - 1][3];
		A[k][1] = A[k - 1][1] + 1;
		break;
	case 'L':
		A[k][0] = A[k - 1][0];
		A[k][1] = A[k - 1][1];
		A[k][3] = A[k - 1][3];
		A[k][2] = A[k - 1][2] + 1;
		break;
	case 'R':
		A[k][0] = A[k - 1][0];
		A[k][1] = A[k - 1][1];
		A[k][2] = A[k - 1][2];
		A[k][3] = A[k - 1][3] + 1;
		break;
	default:
		break;
	}
	if (A[k][1] == A[k][0] && A[k][3] == A[k][2])
		dem++;
}

