const int maxn = 4e5 + 5;
const int maxm = 3e5 + 5;

int N, M, Q;

int par[maxn];
void init(int n) { inc(i, 1, n) par[i] = i; }
int find(int x) {
    if (x == par[x]) return x;
    return par[x] = find(par[x]);
}
bool same(int x, int y) { return find(x) == find(y); }

struct edge {
    int u, v, id;
} e[maxm];
int vis[maxn], node[maxn], root[maxn], vis_point[maxn];
vector<int> g[maxn];

int euler[maxn], in[maxn], out[maxn], top;
int a[maxn], mx[4 * maxn], pos[4 * maxn];
void dfs(int x) {
    vis_point[x] = 1;
    in[x] = ++top;
    if (x <= N) euler[top] = a[x];
    for (int son : g[x]) {
        if (!vis_point[son]) dfs(son);
    }
    out[x] = ++top;
}

void up(int k) {
    mx[k] = max(mx[k << 1], mx[k << 1 | 1]);
    if (mx[k << 1] >= mx[k << 1 | 1])
        pos[k] = pos[k << 1];
    else
        pos[k] = pos[k << 1 | 1];
}
void build(int k, int l, int r) {
    if (l == r) {
        mx[k] = euler[l];
        pos[k] = l;
        return;
    }
    int mid = l + r >> 1;
    build(k << 1, l, mid);
    build(k << 1 | 1, mid + 1, r);
    up(k);
}
pii query(int k, int l, int r, int a, int b) {  //
    if (a <= l && r <= b) {
        return pii(mx[k], pos[k]);
    }
    int mid = l + r >> 1;
    pii res = pii(0, 0);
    if (a <= mid) res = max(res, query(k << 1, l, mid, a, b));
    if (b > mid) res = max(res, query(k << 1 | 1, mid + 1, r, a, b));
    return res;
}
void change(int k, int l, int r, int pos) {
    if (l == r) {
        mx[k] = 0;
        return;
    }
    int mid = l + r >> 1;
    if (pos <= mid)
        change(k << 1, l, mid, pos);
    else
        change(k << 1 | 1, mid + 1, r, pos);
    up(k);
}

