typedef long long LL;

LL dp[20][2525][50];
unordered_map<LL,LL>mp;
LL ve[20];
LL gcd(LL a,LL b)
{
    if(b==0)
        return a;
    return gcd(b,a%b);
}
LL change(LL a,LL b)
{
    if(b==0)
        return a;
    return a/gcd(a,b)*b;
}
LL dfs(LL pos,LL st,LL lca,LL lead,LL op)
{
    if(pos==0)
    {
        if(st%lca==0)
        {
            return 1;
        }
        else
            return 0;
    }
    if((!lead)&&(!op)&&(dp[pos][st][mp[lca]]!=-1))
        return dp[pos][st][mp[lca]];
    LL tmp=op?ve[pos]:9;
    LL ans=0;
    for(Ll i=0;i<=tmp;i++)
    {
        if(lead&&i==0)
        {
            ans+=dfs(pos-1,st,lca,lead,op&&tmp==i);
        }
        else
            if(lead)
                ans+=dfs(pos-1,(st+i)%2520,change(lca,i),0,op&&tmp==i);
            else
                ans+=dfs(pos-1,(st+i)%2520,change(lca,i),0,op&&tmp==i);
    }
    if((!lead)&&(!op))
        dp[pos][st][mp[lca]]=ans;
    return ans;
}
LL solve(string nu)
{
    LL len=0;
    ve[len++]=-1;
    for(LL i=nu.size()-1;i>=0;i---)
    {
        ve[len++]=nu[i]-'0';
    }
    return dfs(len-1,0,1,1,1);
}
