#include<bits/stdc++.h>
using namespace std;
struct node {
	int x, y, z, cnt;
	node(int x,int y,int z, int cnt) : x(x), y(y), z(z), cnt(cnt) {}
	bool operator < (const node & a) const {
		return cnt > a.cnt;
	}
};
bool check(node a) {
	int x = a.x, y = a.y, z = a.z;
	if(x < y + z && y < x + z && z < x + y) return 1;
	return 0;
}
int main() {
	priority_queue<node> q;
	int x, y, z;
	scanf("%d %d %d",&x, &y, &z);
	q.push(node(x,y,z,0));
	while(!q.empty()) {
		node cur = q.top();
		q.pop();
		if(check(cur)) {
			printf("%d\n",cur.cnt);
			return 0;
		}
		cur.cnt++;
		cur.x++;q.push(cur);
		cur.x--;cur.y++;q.push(cur);
		cur.y--;cur.z++;q.push(cur);
	}
}

