ll bin(int pos, int i, int j, ll left, ll right, int it)
{
    int middle = (i+j)/2;
    // if(middle==pos) return fac[n-it];
    if(i>=j && i>0) return fac[n-it];
    else if(middle>pos)
    {
        return (right*(bin(pos, i, middle, left, right-1, it+1))%mod)%mod;
    }
    else
    {
        return (left*(bin(pos, middle, j, left-1, right, it+1))%mod)%mod;
    }
}

