            return false;

    return true;
}
void optimized_seive(ll N)
{
  ll N1;
    isprime[0] = isprime[1] = false ;
    for (ll i=2; i*i<=N ; i++)
    {
        if (isprime[i])
        {
            prime.push_back(i);
            if(N%i==0){factor.push_back(i); N1=N/i;}
            SPF[i] = i;
        }
        for (ll j=0;
             j <(int)prime.size() &&
             i*prime[j] < N && prime[j] <= SPF[i];
             j++)
        {
            isprime[i*prime[j]]=false;
            SPF[i*prime[j]] = prime[j] ;
        }
    }
    //cout<<N1<<endl;
 if((N1*N1)!=N)
    {if(checklast(N1)) factor.push_back(N1);}

}
ll power(ll x, ll m)
{
    ll total = 1;
    x=x%mod;
    if(x==0) return 0;
  while (m>0)
    {
        if (m & 1)
            total =(total*x)%mod;
        m=m>>1;
        x=(x*x)%mod;
    }
    return total;
}
