	Buying(int _w,  int _p): which(_w), profit(_p){}
	bool operator < (Buying b)
	{
		return profit > b.profit;
	}

};
struct Product{
	int bc, sc, r;
};
Product products[20][200];
int main()
{
	int n, m, rem;
	cin >> n >> m >> rem;
	cin.ignore();
	for (int i = 0; i < n; i++)
	{
		string name;
		getline(cin, name);
		for (int j = 0; j < m ;j++)
			cin >> products[i][j].bc >> products[i][j].sc >> products[i][j].r;
		cin.ignore();
	}
	int ans = 0, crem = rem;
	for (int i = 0; i < n; i++)
		for (int j = 0; j < n; j++)
		{
			if (i == j)
				continue;
			Buying benefits[200];
			for (int r = 0; r < m; r++)
				benefits[r] = Buying(r, products[j][r].sc - products[i][r].bc);
			sort(benefits, benefits + m);
			int r = 0;
			int calc = 0;
			while (rem > 0 && r < m)
			{
				if (benefits[r].profit < 0)
					break;
				int dec = min(products[i][benefits[r].which].r, rem);
				calc += dec * benefits[r].profit;
				rem -= dec;
				r++;
			}
			if (calc > ans)
				ans = calc;
			rem = crem;
		}

	cout << ans;
}

