const int N=1000050,mo=1e9+7;
int fap(int a,int b){
  int s=1;
  while(b){
    if (b&1) s=1ll*s*a%mo;
    b>>=1,a=1ll*a*a%mo;
  }
  return s;
}
int f[N],pl[N],ny[N];
int main(){
  int n,k; cin>>n>>k;
  pl[0]=ny[0]=1;
  for (int i=1; i<=n; ++i) pl[i]=1ll*pl[i-1]*i%mo;
  ny[n]=fap(pl[n],mo-2);
  for (int i=n-1; i>=1; --i) ny[i]=1ll*ny[i+1]*(i+1)%mo;
  int ans=0;
  for (int i=k+2; i<=n; ++i){
    f[i]=1ll*(i-k-1)*pl[i-2]%mo;
    for (int j=i-k; j<i; ++j)
      (f[i]+=1ll*c(i-2,j-1)*pl[i-j-1]%mo*f[j]%mo)%=mo;
    (ans+=1ll*f[i]*c(n-1,i-1)%mo*pl[n-i]%mo)%=mo;
  }
  cout<<ans;
  return 0;
}

