using namespace std;
template<class T> T gi() {
	T x = 0; bool f = 0; char c = getchar();
	while (c != '-' && (c < '0' || c > '9')) c = getchar();
	if (c == '-') f = 1, c = getchar();
	while (c >= '0' && c <= '9') x = x * 10 + c - '0', c = getchar();
	return f ? -x : x;
}
const int N = 1e6 + 10, Mod = 1e9 + 7;
int fac[N], ifac[N], f[N];
int fpow(int a, int b) {
	int res = 1;
	for (; b; b >>= 1, a = 1ll * a * a % Mod)
		if (b & 1) res = 1ll * res * a % Mod;
	return res;
}
int C(int n, int m) {
	if (n < m) return 0;
	return 1ll * fac[n] * ifac[m] % Mod * ifac[n - m] % Mod;
}
int main() {
	//freopen(".in", "r", stdin);
	//freopen(".out", "w", stdout);
	int n = gi<int>(), k = gi<int>();
	fac[0] = 1;
	for (int i = 1; i <= n; i++) fac[i] = 1ll * fac[i - 1] * i % Mod; 
	ifac[n] = fpow(fac[n], Mod - 2);
	for (int i = n; i >= 1; i--) ifac[i - 1] = 1ll * ifac[i] * i % Mod;
	f[0] = 1;
	for (int i = 1; i <= n; i++)
		for (int j = 1; j <= k && j <= i; j++)
			(f[i] += 1ll * f[i - j] * C(i - 1, j - 1) % Mod * fac[j - 1] % Mod) %= Mod;
	int ans = 0;
	for (int i = 1; i <= n; i++)
		(ans += 1ll * f[i - 1] * C(n - 1, i - 1) % Mod * fac[n - i] % Mod) %= Mod;
	printf("%d\n", (fac[n] - ans + Mod) % Mod);
	return 0;
}

