void Dfs(int x,int y) {
	if ((x<1)||(x>n)||(y<1)||(y>m)) return;
	if (col[x][y]) return;
	if (vis[x][y]) return;
	vis[x][y]=true; ++tmp[(x+y)&1];
	Dfs(x-1,y); Dfs(x+1,y);
	Dfs(x,y-1); Dfs(x,y+1);
}

bool Greed(int x1,int y1,int x2,int y2) {
	col[x1][y1]=col[x2][y2]=true;
	memset(vis,false,sizeof(vis));
	for (int i=1;i<=n;++i)
		for (int j=1;j<=m;++j)
			if ((!col[i][j])&&(!vis[i][j])) {
				tmp[0]=tmp[1]=0;
				Dfs(i,j);
				if (tmp[0]!=tmp[1])
					{col[x1][y1]=col[x2][y2]=false;return false;}
			}
	return true;
}

void Search(int k,int sum) {
	if (k==w+1) {ans=max(ans,sum);return;}
	if (w-k+10+sum<=ans) return;
	bool flag=Greed(b[k][0],b[k][1],b[k][2],b[k][3]);
	if (flag) Search(k+1,sum+1);
	col[b[k][0]][b[k][1]]=false;
	col[b[k][2]][b[k][3]]=false;
	Search(k+1,sum);
}

