inline bool look(int x, int y)
{
	if (mark[x][y]) return 0;
	mark[x][y] = 1;
	if (p[x][y] == -1) return 1;
	int xx = p[x][y] / m;
	int yy = p[x][y] % m;
	for (int k = 0; k < 4; ++k)
	{
		int sx = xx + dx[k];
		int sy = yy + dy[k];
		if (0 <= sx && sx < n && 0 <= sy && sy < m && c[sx][sy] && look(sx, sy))
		{
			p[xx][yy] = sx * m + sy;
			p[sx][sy] = xx * m + yy;
			return 1;
		}
	}
	return 0;
}

inline void go(int v)
{
