#include <bits/stdc++.h>
using namespace std;

#define ll long long

const int MAXI = 2e9;

const int N = 1000;

int fac[N];

int org(int x){
   int ret = 0;
   queue<int> q;
   q.push(0);
   while(!q.empty()){
      int u = q.front();
      q.pop();
      int v= u*10 + 4;
      int v2 = u*10 + 7;
      if(v<=x){
         q.push(v);
         ++ret;
      }
      if(v2<=x){
         q.push(v2);
         ++ret;
      }
   }
   return ret;
}

bool isLucky(int x){
   while(x>0){
      int g = x%10;
      if(g==4 || g==7){
         x/=10;
      } else {
         return false;
      }
   }
   return true;
}
void solve() {
   int n,k;
   scanf("%d%d",&n,&k);
   ll ret = 1;
   int ind = 0,l = 0,r = n,mid;
   bool isSatisfy = false;
   for(int i=1;i<=n;++i){
      ret*=i;
      if(ret>=k){
         isSatisfy = true;
         break;
      }
   }
   fac[0] = 1;
   for(int i=1;i<N;++i){
      fac[i] = fac[i-1]*i;
   }
   if(!isSatisfy){
      printf("-1\n");
      return;
   }
   while(l<r) {
      mid = (l+r+1)/2;
      bool isGood = false;
      ll tmp = 1;
      for(int i=1;i<= n - mid;++i){
         tmp*=i;
         if(tmp>=k){
            isGood = true;
            break;
         }
      }
      if(!isGood){
         r = mid -1;
      } else {
         l = mid;
      }
   }
   int R = org(l);
   set<int> s;
   for(int i=l+1;i<=n;++i){
      s.insert(i);
   }
   ind = l+1;
   while(!s.empty()){
      int rem = s.size();
      int g = 0;
      while( g+1 <rem){
         ll v = fac[rem-1]*(g+1);
         if(v<k){
            ++g;
         } else {
            break;
         }
      }
      k -= g*fac[rem-1];
      for(set<int>::iterator it = s.begin();it!= s.end();++it){
         if(g==0){
            if(isLucky(*it)&& isLucky(ind)){
               ++R;
            }
            s.erase(it);
            break;
         } else {
            --g;
         }
      }
      ++ind;
   }
   printf("%d\n", R);
}

int main(){
   //freopen("input.txt","r",stdin);
   solve();
}

