const int INF = 1e9;
const ll LINF = 1e18;

vector<int> t;
int sz;

void update(int pos, int val){
    pos += sz - 1;
    t[pos] = val;
    while (pos != 0) {
        pos = (pos - 1) / 2;
        t[pos] = max(t[2 * pos + 1], t[2 * pos + 2]);
    }
}

int get(int l, int r, int x = 0, int tl = 0, int tr = sz){
    if(l >= tr || r <= tl)
        return 0;
    if(l <= tl && r >= tr)
        return t[x];
    int m = (tl + tr) / 2;
    int x1 = get(l, r, 2 * x + 1, tl, m);
    int x2 = get(l, r, 2 * x + 2, m, tr);
    return max(x2, x1);
}

