const int maxnum = 10005;
int c[maxnum][maxnum];
int fac[maxnum];
int n, x, pos;
// c[i][j]   i   j 
void init() {
	c[0][0] = 1;
	c[1][0] = c[1][1] = 1;
	for (int i = 2; i < maxnum; i++) {
		c[i][0] = 1;
		for (int j = 1; j < maxnum; j++) {
			c[i][j] = c[i - 1][j] + c[i - 1][j - 1];
			c[i][j] %= mod;
		}
	}
	fac[0] = 1;
	fac[1] = 1;
	for (int i = 2; i < maxnum; i++) {
		fac[i] = i * fac[i - 1] % mod;
		fac[i] %= mod;
	}
}

int A(int a, int b) {
	return (fac[a] / fac[a - b]) % mod;
}
int C(int a, int b) {
	return c[b][a] % mod;
}
signed main()
{
	init();
	n = read(), x = read(), pos = read();
	int L = 0, R = n;
	int MIN = 0, MAX = 0;
	while (L < R) {
		int mid = (L + R) / 2;
		if (pos < mid) {
			MAX++;
			R = mid;
		}
		else  if (pos > mid) {
			MIN++;
			L = mid + 1;
		}
		else {
			break;
		}
	}
	//cout << MAX << "\t" << MIN << endl;
	int ans = 1;
	ans *= (C(MAX, n-x)*A(MAX, MAX)) % mod; ans %= mod;
	ans *= (C(MIN, x-1)*A(MIN, MIN)) % mod; ans %= mod;
	ans *= A(n - MAX - MIN -1, n - MAX - MIN - 1) % mod;
	ans %= mod;
	cout << ans << endl;
	return 0;
}

