int f(int start){
    if(start>=n) return 0;

    int c=0, pointer=start;
    for(int i=start;i<n;i=ll[i]){
        if(b[i]==b[pointer]) c++;
        else if(c>2){
            if(pointer>start) ll[pointer-1]=i;
            else start=i;
        }
        else {
            pointer=i; c=1;
        }
    }

    if(c>2){
        if(pointer>start) ll[pointer-1]=n;
        else start=n;
    }

    c+=f(start);

    return c;
}

