#define mod 1000000007
#define ll long long int

ll fac(ll x) {
    if (x == 0)
        return 1;
    return ((x % mod) * fac(x - 1) % mod) % mod;
}

ll power(ll x, ll y, ll p)
{
    ll res = 1;

    x = x % p; 

    while (y > 0) {
        if (y & 1)
            res = (res * x) % p;

        y = y >> 1;
        x = (x * x) % p;
    }
    return res;
}

ll modInverse(ll n, ll p)
{
    return power(n, p - 2, p);
}
ll nPrModPFermat(ll n, ll r, ll p)
{
    if (r == 0)
        return 1;

    return ((fac(n)%p)  * modInverse(fac(n-r), p) % p) % p;
}

void solve()
{
    ll n, x, pos;
    cin >> n >> x >> pos;
    ll left = 0, right = 0;
    ll l = 0, r = n;
    while (l < r)
    {
        ll mid = l + (r - l) / 2;
        if (mid < pos)
        {
            left++;
            l = mid + 1;
        }
        else if (mid > pos)
        {
            right++;
            r = mid;
        }
        else
            break;
    }

    ll total = (ll)right + left;
    ll less = (ll)x - 1ll;
    ll more = (ll)n - x;
    ll ans = (ll)(((ll)(nPrModPFermat(less,left,mod) % mod) * (ll)(nPrModPFermat(more,right,mod) % mod))%mod * (ll)(fac(n-total-1)%mod)) % mod;
    cout << ans << "\n";



}

int main()
{
    ios_base::sync_with_stdio(false), cin.tie(NULL), cout.tie(NULL);

    solve();


    return 0;
}

