int bigmod(int a,int b,int mod){
	int res=1;
	a=(a%mod);
	while(b>0){
		if(b&1)
		   res=(res*1LL*a)%mod;
		a=(a*1LL*a)%mod;
		b/=2;
	}
	return res;
}
