int findLevel(ii s,ii d,ii moves)
{
	queue<ii> q;
	q.push(mp(s.fi,s.se));
	while(!q.empty())
	{
		ii node=q.front();
		q.pop();
		if(node.fi-moves.fi>=(-1)*1e5&&node.se-moves.se>=(-1)*1e5)
		{
			if(node.fi-moves.fi==d.fi&&node.se-moves.se==d.se)
				return 1;
			else
				q.push(mp(node.fi-moves.fi,node.se-moves.se));
		}
		if(node.fi-moves.fi>=(-1)*1e5&&node.se+moves.se<=1e5)
		{
			if(node.fi-moves.fi==d.fi&&node.se+moves.se==d.se)
				return 1;
			else
				q.push(mp(node.fi-moves.fi,node.se+moves.se));
		}
		if(node.fi+moves.fi<=1e5&&node.se-moves.se>=(-1)*1e5)
		{
			if(node.fi+moves.fi==d.fi&&node.se-moves.se==d.se)
				return 1;
			else
				q.push(mp(node.fi+moves.fi,node.se-moves.se));
		}
		if(node.fi+moves.fi<=1e5&&node.se+moves.se<=1e5)
		{
			if(node.fi+moves.fi==d.fi&&node.se+moves.se==d.se)
				return 1;
			else
				q.push(mp(node.fi+moves.fi,node.se+moves.se));
		}
	}
	return 0;
}

