string clean(string hello)
{
    for(int i=0; i<hello.size(); i++)
        if(hello[i]!='h'&&hello[i]!='e'&&hello[i]!='l'&&hello[i]!='o')
        {
            hello.erase(i,1);
            i--;
        }
    return hello;
}


