		f0[i]=g[i]*sqr[i]%mod;
		f1[i]=(g[i]*sqr[i+1]+f0[i-1])%mod;
		f2[i]=(g[i]*sqr[i+2]+f1[i-1]+(i>1)*g[i-1]*sqr[i])%mod;
		for(int j=2;j<i;++j)
		{
			f0[i]+=g[j-1]*sqr[j-1]%mod*f0[i-j]%mod;
			f1[i]+=g[j-1]*sqr[j]%mod*f0[i-j]%mod;
			f2[i]+=g[j-1]*sqr[j]%mod*f1[i-j]%mod;
			f1[i]+=g[j-2]*sqr[j]%mod*f1[i-j-1]%mod;
			f0[i]+=g[j-2]*sqr[j-1]%mod*f1[i-j-1]%mod;
			f2[i]+=g[j-2]*sqr[j]%mod*f2[i-j-1]%mod;
		}
		f0[i]%=mod;f1[i]%=mod;f2[i]%=mod;
	}
	ans=sqr[n-1]*(g[n-1]+g[n-3])%mod*n%mod;
	for(int i=3;i<n;++i)
	{
		ans+=sqr[i-2]*g[i-2]%mod*f0[n-i]%mod*(i-1)%mod;
		ans+=sqr[i-2]*g[i-3]%mod*f1[n-i-1]%mod*2*(i-1)%mod;
		if(i>=4 && i+1<n)ans+=sqr[i-2]*g[i-4]%mod*f2[n-i-2]%mod*(i-1)%mod;
	}
	printf("%lld\n",ans%mod);
}

