int cursz = 0;

int bin(int x) {
    int l = 0, r = cursz - 1;
    int res = -1;
    while (l <= r) {
        int mid = (l + r) / 2;
        if (dp[mid] > x) {
            res = mid;
            r = mid - 1;
        } else l = mid + 1;
    }
    return res;
}

