inline long long extend_gcd(long long a,long long b,long long &x,long long &y)
{
    if(a==0&&b==0)
        return -1ll;
    if(b==0)
    {
        x=1ll;
        y=0ll;
        return a;
    }
    long long d=extend_gcd(b,a%b,y,x);
    y-=a/b*x;
    return d;
}
inline long long ny(long long a,long long n)
{
    long long x,y,d=extend_gcd(a,n,x,y);
    if(d==1)
        {
                    if(x%n<=0)return x%n+n;
                    else return x%n;
                 }
    else
        return -1ll;
}
long long prime[1000010];
int pnum;
bool vis[50001000];
void getprime()
{
	pnum=0;
	long long gg=n;
	for(long long i=2;i<=10000000;i++)
	{
		if(!vis[i])
		{
			prime[++pnum]=i;
			while(gg%i==0) gg/=i;
		}
		for(int j=1;j<=pnum&&i*prime[j]<=10000000;j++)
		{
			vis[i*prime[j]]=1;
			if(i%prime[j]==0) break;
		}
	}
	if(gg>10000000)
	{
		prime[++pnum]=gg;
	}
}
