void getans(int x,long long now,int value)
{
	if(x==num)
	{
		ans=(ans+value*now%MOD)%MOD;
		return ;
	}
	for(int i=0;i<=f[x];i++)
	{
		getans(x+1,now*pow(a[x],i)%MOD,value*dp[K][x][i]%MOD);
	}
}
