    for(i=2;i<=tn;i++)
    {
        if(!np[i])  pri[++num]=i;
        for(j=1;j<=num&&i*pri[j]<=tn;j++)
        {
            np[i*pri[j]]=1;
            if(i%pri[j]==0) break;
        }
