using namespace std;

int N, M, K, prefix[102], automaton[102][26], dp[102][102][102], p[102][102][102];
char s1[102], s2[102], virus[102], ans[102];

int main() {
  scanf("%s%s%s", s1, s2, virus);
  N = strlen(s1);
  M = strlen(s2);
  K = strlen(virus);

  {
    int k = 0;
    FOR (i, K) {
      while (k > 0 && virus[k + 1] != virus[i]) k = prefix[k];
      if (virus[k + 1] == virus[i]) ++k;
      prefix[i + 1] = k;
    }

    FOR (i, K) FOR (c, 26) {
      int x = i;
      while (x > 0 && virus[x] != c + 'A') x = prefix[x];
      if (virus[x] == c + 'A') ++x;
      automaton[i][c] = x;
    }
  }

  memset(dp, -1, sizeof dp);
  dp[0][0][0] = 0;
  FOR (i, N + 1) FOR (j, M + 1) FOR (k, K) {
    if (dp[i][j][k] == -1) continue;
    if (dp[i + 1][j][k] < dp[i][j][k]) {
      dp[i + 1][j][k] = dp[i][j][k];
      p[i + 1][j][k] = 1;
    }
    if (dp[i][j + 1][k] < dp[i][j][k]) {
      dp[i][j + 1][k] = dp[i][j][k];
      p[i][j + 1][k] = 2;
    }
    int nk = automaton[k][s1[i] - 'A'];
    if (s1[i] == s2[j] && nk != K) {
      if (dp[i + 1][j + 1][nk] < dp[i][j][k] + 1) {
        dp[i + 1][j + 1][nk] = dp[i][j][k] + 1;
        p[i + 1][j + 1][nk] = 3 + k;
      }
    }
  }

  int ck = max_element(dp[N][M], dp[N][M] + K) - dp[N][M];
  if (dp[N][M][ck]) {
    for (int ci = N, cj = M, k = dp[ci][cj][ck]; k; ) {
      if (p[ci][cj][ck] == 1) --ci;
      else if (p[ci][cj][ck] == 2) --cj;
      else {
        ck = p[ci][cj][ck] - 3;
        --ci; --cj;
        ans[--k] = s1[ci];
      }
    }
    printf("%s\n", ans);
  } else printf("0\n");

  return 0;
}

