int go(int N, int M, int L)
{
	if (L == l)return -INF;
	if (N == n || M == m)return 0;
	int &ret = d[N][M][L];
	if (ret != -1)return ret;
	ret = 0;
	ret = max(ret, go(N + 1, M, L));
	ret = max(ret, go(N, M + 1, L));
	if (a[N] == b[M])
	{
		if (a[N] == c[L])ret = max(ret, 1 + go(N + 1, M + 1, L + 1));
		else
		{
			while (L != 0 && a[N] != c[pi[L - 1]])L = pi[L];
			if (a[N] == c[L])ret = max(ret, 1 + go(N + 1, M + 1, L + 1));
			else ret = max(ret, 1 + go(N + 1, M + 1, L));
		}
	}
	return ret;
}
void track(int N, int M, int L)
{
	if (N == n || M == m)return;
	int t1 = go(N + 1, M, L);
	int t2 = go(N, M + 1, L);
	int t3 = -INF, t4 = -INF, t5 = -INF;
	if (a[N] == b[M])
	{
		if (a[N] == c[L])t3 = 1 + go(N + 1, M + 1, L + 1);
		else
		{
			while (L != 0 && a[N] != c[pi[L - 1]])L = pi[L];
			if (a[N] == c[L])t4 = go(N + 1, M + 1, L + 1);
			else t5 = 1 + go(N + 1, M + 1, L);
		}
	}
	int MAX = max({ t1,t2,t3,t4,t5 });
	if (t1 == MAX)track(N + 1, M, L);
	else if (t2 == MAX)track(N, M + 1, L);
	else if (t3 == MAX)
	{
		printf("%c", a[N]);
		track(N + 1, M + 1, L + 1);
	}
	else if (t4 == MAX)
	{
		printf("%c", a[N]);
		track(N + 1, M + 1, 1);
	}
	else
	{
		printf("%c", a[N]);
		track(N + 1, M + 1, 0);
	}
}
