int dfs(int h,int num,bool flag){
    if(num<0) return 0;
    if(h==-1) return num==0;
    if(!flag && dp[h][num]!=-1) return dp[h][num];
    int up = flag?p[h]:9;
    int res= 0;
    for(int i=0;i<=up;i++){
        if(i==4 || i==7) res += dfs(h-1,num-1,flag&&i==up);
        else res += dfs(h-1,num,flag&&i==up);
    }
    if(!flag) dp[h][num] = res;
    return res;
}

int solve(int k,int a){
    memset(dp,-1,sizeof(dp));
    int cnt=0;
    while(k){
        p[cnt++] = k%10;
        k /= 10;
    }
    return dfs(cnt-1,a,true);
}

long long F(int n,int m){
    long long res = 1;
    for(int i=0;i<m;i++) res = (res*(n-i))%MOD;
    return res%MOD;
}

