    //cout<< p << " "<<sum <<" "<<t <<" "<<tot<<endl;
    if(sum>=t && p!=0) return 0;
    if(p==7){
        long long res = 1;
        for(int i=0;i<=tot;i++){
            //cout << "##" <<pp[i] <<" " <<y[i] <<endl;
            res = res*F(pp[i],y[i])%MOD;
        }
        return res;
    }

    long long ans = 0;
    for(int i=0;i<=tot;i++){
        //cout << "i " <<i <<endl;
        y[i]++;
        if(p==0) {
            ans += dfs(p+1,sum,i,y,tot);
            ans%=MOD;
        }else{
            ans += dfs(p+1,sum+i,t,y,tot);
            ans%=MOD;
        }
        y[i]--;
    }
    return ans;
}

