template <class T> inline void smin(T &a, T b) {if (a>b) a=b;}
template <class T> inline void smax(T &a, T b) {if (a<b) a=b;}
#define NN 22
#define inf 0x3f3f3f3f

int N, a[NN], b[NN];
int f[NN][NN], g[NN][NN];

int calc(int g[NN][NN], int f[NN][NN], int n) {
	int i, j, k, tmp[NN][NN];

	if (n==0) {
		for (i=0; i<=N; i++) for (j=0; j<=N; j++) f[i][j]=inf;
		for (i=0; i<=N; i++) f[i][i]=0;
		return 0;
	}

	calc(g, tmp, n/2);

	for (i=0; i<=N; i++) for (j=0; j<=N; j++) {
		f[i][j]=inf;
		for (k=0; k<=N; k++) smin(f[i][j], tmp[i][k]+tmp[k][j]);
	}

	if (n%2) {
		for (i=0; i<=N; i++) for (j=0; j<=N; j++) {
			tmp[i][j]=inf;
			for (k=0; k<=N; k++) smin(tmp[i][j], f[i][k]+g[k][j]);
		}
		for (i=0; i<=N; i++) for (j=0; j<=N; j++) f[i][j]=tmp[i][j];
	}
}

int main() {

	int bit, M, i, s, deep, cnt, u, v;

	scanf("%d%d", &N, &M);
	for (i=0; i<N; i++) scanf("%d", a+i);
	for (i=0; i<N; i++) scanf("%d", b+i);

	memset(g, 0x3f, sizeof(g));

	for (bit=0; bit<(1<<N); bit++) {
		s=deep=cnt0;

		for (i=0; i<N; i++) {
			if (bit&(1<<i)) {
				s+=a[i]; deep++;
			} else {
				s+=b[i]; deep--;
			}

			smin(cnt, deep);
		}

		for (int j=-cnt; j<=N; j++) {
			u=j; v=j+deep;
			if (v<=N && v>=0) smin(g[u][v], s);
		}
	}

	calc(g, f, M);
	printf("%d\n", f[0][0]);

	return 0;
}

