const int INF = 1000000000;

class costflow {
    int s[Nm], q[Nm], pre[Nm], v[Nm]; // Ns
    int t[Mm], next[Mm], cf[Mm]; // Ms
    int w[Mm], d[Nm];
    int cnt;
    int N;

public:

    void init(int n) {
        cnt = 1;
        N = n;
        memset(s, 0, sizeof (s));
        memset(d, 0, sizeof (d));
        memset(q, 0, sizeof (q));
        memset(pre, 0, sizeof (pre));
        memset(v, 0, sizeof (v));

        memset(t, 0, sizeof (t));
        memset(cf, 0, sizeof (cf));
        memset(w, 0, sizeof (w));
        memset(next, 0, sizeof (next));
    }

    void add(int ss, int tt, int cc, int ww) {
        ++cnt;
        t[cnt] = tt;
        cf[cnt] = cc;
        w[cnt] = ww;
        next[cnt] = s[ss];
        s[ss] = cnt;
        ++cnt;
        t[cnt] = ss;
        cf[cnt] = 0;
        w[cnt] = -ww;
        next[cnt] = s[tt];
        s[tt] = cnt;
    }

    pair<int, int> getflow(int S, int T) {
        int cost = 0;
        int flow = 0;
        int i, j, head, tail;
        while (true) {
            head = 0;
            tail = 1;
            for (i = 0; i < N; i++) {
                d[i] = INF;
            }
            for (i = 0; i < N; i++) {
                v[i] = 0;
            }
            d[S] = 0;
            q[1] = S;
            while (head != tail) {
                v[i = q[head = (head + 1) % N]] = 0;
                for (j = s[i]; j != 0; j = next[j]) {
                    if (cf[j] != 0 && d[t[j]] > d[i] + w[j]) {
                        d[t[j]] = d[i] + w[j];
                        pre[t[j]] = j;
                        if (v[t[j]] == 0) {
                            v[t[j]] = 1;
                            q[tail = (tail + 1) % N] = t[j];
                        }
                    }
                }
            }
            if (d[T] == INF) {
                return make_pair(flow, cost);
            }
            cost += d[T];
            ++flow;
            for (i = T; i != S; i = t[pre[i] ^ 1]) {
                --cf[pre[i]];
                ++cf[pre[i] ^ 1];
            }
        }
    }
} network;

