int ksm(int x,int k){
	int ans=1;
	while(k){
		if(k&1)ans=1ll*ans*x%mod;
		x=1ll*x*x%mod;
		k>>=1;
	}
	return ans;
}
int jc[maxn],inv[maxn];
void init(int n){
	jc[0]=1;
	fr(i,1,n)jc[i]=1ll*jc[i-1]*i%mod;
	inv[n]=ksm(jc[n],mod-2);
	fd(i,n-1,0)inv[i]=1ll*inv[i+1]*(i+1)%mod;	
}
int C(int n,int m){
	if(n<0||m<0||n<m)return 0;
	return 1ll*jc[n]*inv[n-m]%mod*inv[m]%mod;
}
