	vector<int> C(A.size()+B.size()-1);
	fr(i,0,A.size()-1)fr(j,0,B.size()-1)add(C[i+j],1ll*A[i]*B[j]%mod);
	return C;
}
int G(int n,int m){
	return C(m+n-1,n-1);	
}
