			if(l==r){
				f[l][r]=0,g[l][r]=2;
			}
			else{
				g[l][r]=2ll*(g[l+1][r]+g[l][r-1])%p;
				for(int x=l+1;x<=r;x++){
					f[l][r]=(f[l][r]+1ll*f[l+1][r]+1ll*g[l+1][r]*(x-l))%p;
					g[l][r]=(g[l][r]+g[l+1][r])%p;
				}
				for(int x=l;x<=r-1;x++){
					f[l][r]=(f[l][r]+1ll*f[l][r-1]+1ll*g[l][r-1]*(r-x))%p;
					g[l][r]=(g[l][r]+g[l][r-1])%p;
				}
				f[l][r]=(f[l][r]+f[l+1][r]*2ll+f[l][r-1]*2ll)%p;
				for(int x=l+1;x<=r-1;x++){
					f[l][r]=(f[l][r]+1ll*(1ll*f[l][x-1]*g[x+1][r]%p+1ll*f[x+1][r]*g[l][x-1]%p)*(r-l+2)%p*binom(r-l,x-l))%p;
					f[l][r]=(f[l][r]+1ll*(getsum(x-l)+getsum(r-x))*g[x+1][r]%p*g[l][x-1]%p*binom(r-l,x-l))%p;
					g[l][r]=(g[l][r]+1ll*g[x+1][r]%p*g[l][x-1]%p*binom(r-l,x-l)%p*(r-l+2))%p;
				}
			}
