				ans[j+1][t]=(ans[j+1][t]+1ll*ans[i-1][k]*g[i][j]%p*binom(t,j-i+1))%p;
				ans[j+1][t]=(ans[j+1][t]+1ll*f[i][j]*sum[i-1][k]%p*binom(t,j-i+1))%p;
				sum[j+1][t]=(sum[j+1][t]+1ll*g[i][j]*sum[i-1][k]%p*binom(t,j-i+1))%p;
			}
		for(int j=0;j<=n;j++){
			ans[i][j]=(ans[i][j]+ans[i-1][j])%p;
			sum[i][j]=(sum[i][j]+sum[i-1][j])%p;
		}
	}
	cout<<(ans[n+1][m]+ans[n][m])%p<<endl;
}
