static const LL INF = 0x3fffffffffffffffLL;

template<typename T>
vector<T> MATMUL(const vector< vector<T> >& a, const vector<T>& v)
{
	int N = a.size();
	vector<T> u(N, INF);
	for(int i=0; i<N; ++i)
	for(int j=0; j<N; ++j)
		u[i] = min(u[i], a[i][j]+v[j]);
	return u;
}

template<typename T>
vector< vector<T> > MATMUL(const vector< vector<T> >& a, const vector< vector<T> >& b)
{
	int N = a.size();
	vector< vector<T> > c(N, vector<T>(N, INF));
	for(int i=0; i<N; ++i)
	for(int j=0; j<N; ++j)
	for(int k=0; k<N; ++k)
		c[i][j] = min(c[i][j], a[i][k]+b[k][j]);
	return c;
}

template<typename T>
vector<T> MATPOWMUL(vector< vector<T> > a, LL e, vector<T> v)
{
	for(; e; e>>=1, a=MATMUL(a,a))
		if(e&1)
			v = MATMUL(a, v);
	return v;
}

int solve(const vector<int>& a, const vector<int>& b, int n, int m)
{
	vector<vector<LL>> blk(n+1, vector<LL>(n+1, INF));

	for(int pat=0; pat<(1<<n); ++pat)
	{
		int cur=0, bot=0, cost=0;
		for(int i=0; i<n; ++i)
			if(pat & (1<<i))
				cur++, cost+=a[i];
			else
				bot=min(bot, --cur), cost+=b[i];
		blk[0-bot][cur-bot] = min(blk[0-bot][cur-bot], cost);
	}

	vector<vector<LL>> M(2*n, vector<int>(2*n, INF));
	for(int s=0; s<2*n; ++s)
		for(int bs=0; bs<=n; ++bs)
		for(int be=0; be<=n; ++be) {
			int e = s-bs+be;
			if(bs<=s && 0<=e && e<2*n)
				M[e][s] = min(M[e][s], blk[bs][be]);
		}

	vector<LL> V(2*n, INF);
	V[0] = 0;

	V = MATPOWMUL(M, m, V);
	return (int)V[0];
}

int main()
{
	for(int n,m; cin>>n>>m; )
	{
		vector<int> a(n), b(n);
		for(int& ai: a) cin>>ai;
		for(int& bi: b) cin>>bi;
		cout << solve(a, b, n, m) << endl;
	}
}

