using namespace std;

typedef pair<int, pair<int, int> > pii;
const int MAX = 1000000 + 2;
bool vis[MAX];
int ans[MAX];
int top = 0;

class Treap
{
	private:
	struct node {
		pii val;
		int key;
		int size;
		node *child[2];

		int rnd() {
			static int KEY = 12345678;
			return KEY += KEY << 2 | 1;
		}

		node(pii _val, node *c) {
			val = _val;
			key = rnd();
			child[0] = child[1] = c;
			size = 1;
		}

		void rz() {
			size = child[0]->size + child[1]->size + 1;
		}
	};

	node *root, *Null;
	void rotate(node *&t, bool d);
	void _insert(node *&t, pii x);
	void _del(node *&t, pii x);
	pii _kth(node *&t, int x);

	public:
	Treap();
	void insert(pii x);
	void del(pii x);
	pii kth(int x);
	int size();
	pii Min();
} T;

void Treap::rotate(node *&t, bool d)
{
	node *p = t->child[d];
	t->child[d] = p->child[!d];
	p->child[!d] = t;
	t->rz();
	p->rz();
	t = p;
}

void Treap::_insert(node *&t, pii x)
{
	if (t == Null) {
		t = new node(x, Null);
	}
	else {
		bool d = x > t->val;
		_insert(t->child[d], x);
		if (t->child[d]->key < t->key) {
			rotate(t, d);
		}
		else {
			t->rz();
		}
	}
}

void Treap::_del(node *&t, pii x)
{
	if (t != Null) {
		if (t->val == x) {
			bool d = t->child[0]->key > t->child[1]->key;
			if (t->child[d] == Null) {
				delete t;
				t = Null;
				return;
			}
			rotate(t, d);
			_del(t->child[!d], x);
		}
		else {
			bool d = x > t->val;
			_del(t->child[d], x);
		}
		t->rz();
	}
}

pii Treap::_kth(node *&t, int x)
{
	int r = t->child[0]->size;
	if (x == r) {
		return t->val;
	}
	else if (x < r) {
		return _kth(t->child[0], x);
	}
	else {
		return _kth(t->child[1], x - r - 1);
	}
}

Treap::Treap()
{
	Null = new node(pii(0, mp(0, 0)), 0);
	Null->size = 0;
	Null->key = INT_MAX;
	Null->child[0] = Null->child[1] = Null;
	root = Null;
}

void Treap::insert(pii x)
{
	_insert(root, x);
}

void Treap::del(pii x)
{
	_del(root, x);
}

pii Treap::kth(int x)
{
	return _kth(root, x);
}

int Treap::size()
{
	return root->size;
}

pii Treap::Min()
{
	node *p = root;
	while (p->child[0] != Null) {
		p = p->child[0];
	}
	return p->val;
}

