#include <algorithm>
using namespace std;

#define Max(a,b) (((a)>(b))?(a):(b))
const int maxn=1000005;

template<typename T>
struct Myset {
    struct Node{
        int sz, hei;
        Node *son[2];
        T val;

        Node():sz(1), hei(1) { son[0]=son[1]=NULL; }
    } *root;

    Myset():root(NULL) {}

    int height(Node *&node) {
        return (NULL==node)?0:node->hei;
    }

    int size(Node *&node) {
        return (NULL==node)?0:node->sz;
    }

    void fix(Node *&node) {
        node->hei=Max(height(node->son[0]), 
                    height(node->son[1]))+1;
        node->sz=size(node->son[0])+size(node->son[1])+1;
    }

    void rotate(Node *&node, int rodir) {
        Node *pivot=node->son[1-rodir];
        node->son[1-rodir]=pivot->son[rodir];
        pivot->son[rodir]=node;
        if (node==root) root=pivot;
        node=pivot;

        fix(node->son[rodir]);
        fix(node);
    }

    void balance(Node *&node) {
        int dif=height(node->son[1])-height(node->son[0]);
        if (dif<-1 || dif>1) {
            int rodir=(dif>0)?0:1, dir=1-rodir;
            if (height(node->son[dir]->son[1-dir])
                -height(node->son[dir]->son[dir])>1)
                rotate(node->son[dir], dir);
            rotate(node, rodir);
        }
    }

    void insert(T &val, Node *&node) {
        if (NULL==node) {
            node=new Node();
            node->val=val;
            return;
        }
        if (val<=node->val) insert(val, node->son[0]);
        else insert(val, node->son[1]);
        fix(node);
        // balance(node);
    }

    void erase(T &val, Node *&node) {
        if (NULL==node) return;
        if (val==node->val) {
            if (NULL==node->son[0]) {
                if (root==node) root=node=node->son[1];
                else node=node->son[1];
                return;
            } else if (NULL==node->son[1]) {
                if (root==node) root=node=node->son[0];
                else node=node->son[0];
                return;
            } else if (NULL==node->son[0] && NULL==node->son[1]) {
                node=NULL;
                return;
            }

            Node *suc=node->son[1], *sucPa=node;
            while (NULL!=suc->son[0]) { sucPa=suc; suc=suc->son[0]; }
            swap(suc->val, node->val);
            sucPa->son[0]=NULL;
            erase(val, node->son[1]);
        } 
        else if (val<node->val) erase(val, node->son[0]);
        else erase(val, node->son[1]);
        fix(node);
        // balance(node);
    }

    T valAt(int rnk, Node *&node) {
        if (rnk>size(node)) return T();
        if (rnk<=size(node->son[0])) return valAt(rnk, node->son[0]);
        else if (rnk==1+size(node->son[0])) return node->val;
        else return valAt(rnk-1-size(node->son[0]), node->son[1]);
    }

    void insert(T val) { insert(val, root); }
    void erase(T val) { erase(val, root); }
    T valAt(int rnk) { return valAt(rnk, root); }
};

Myset<int> pos;
Myset<pair<int,int> > rec;
int vis[maxn], ans[maxn];

int main() {
    // freopen("in.txt", "r", stdin);

    int n, m;
    scanf("%d%d", &n, &m);
    memset(vis, 0, sizeof(vis));
    memset(ans, -1, sizeof(ans));
    for (int i=1; i<=n; ++i) pos.insert(i);

    int sum=0;
    while (m--) {
        int x, y;
        scanf("%d%d", &x, &y);
        if (!vis[x]) {
            if (y<=sum) { printf("-1\n"); return 0; }
            ans[pos.valAt(y-sum)]=x;
            rec.insert(make_pair(m,x));
            pos.erase(pos.valAt(y-sum));
            vis[x]=1;
            ++sum;
        } else {
            if (y>rec.size(rec.root) || rec.valAt(y).second!=x) {
                printf("-1\n"); return 0;
            }
            rec.erase(rec.valAt(y));
            rec.insert(make_pair(m,x));
        }
    }
    int j=1;
    for (int i=1; i<=n; ++i) {
        if (-1==ans[i]) {
            while (vis[j]) ++j;
            vis[j]=1;
            ans[i]=j;
        }
    }
    for (int i=1; i<=n; ++i) {
        if (i-1) printf(" ");
        printf("%d", ans[i]);
    }
    printf("\n");
    return 0;
}

#include <iostream>
using namespace std;

int n, m, x, y;

void init(int *itChecked, int *itDistinc, int *posIT, int lef, int rig, int k){
    itChecked[k] = 0;
    itDistinc[k] = 0;
    if (lef == rig){
        posIT[lef] = k;
        return;
    }
    init(itChecked, itDistinc, posIT, lef, (lef + rig) >> 1, k << 1);
    init(itChecked, itDistinc, posIT, ((lef + rig) >> 1) + 1, rig, (k << 1) + 1);
}

void update(int *it, int posIT, int val){
    while (posIT){
        it[posIT] += val;
        posIT >>= 1;
    }
}

int find(int *itChecked, int lef, int rig, int k, int vt){
    if (lef == rig)
        return lef;

    int mid = (rig + lef) >> 1;
    if (mid + itChecked[(k << 1) + 1] < vt)
        return find(itChecked, mid + 1, rig, (k << 1) + 1, vt);
    else
        return find(itChecked, lef, mid, k << 1, vt - itChecked[(k << 1) + 1]);
}

int get(int *it, int lef, int rig, int u, int v, int k){
    if (u > v)
        return 0;
    if (lef == u && rig == v)
        return it[k];

    int mid = (lef + rig) >> 1;
    if (v <= mid)
        return get(it, lef, mid, u, v, k << 1);
    else
    if (u > mid)
        return get(it, mid + 1, rig, u, v, (k << 1) + 1);
    else
        return get(it, lef, mid, u, mid, k << 1) + get(it, mid + 1, rig, mid + 1, v, (k << 1) + 1);
}

int main(){
    ios_base::sync_with_stdio(false);
    cin >> n >> m;
    int *itChecked = new int[5 * n];
    int *itDistinc = new int[5 * n];
    int *posIT = new int[n + 1];
    int *posElement = new int[n + 1];
    bool *exist = new bool[n + 1];
    int *res = new int[n + 1];
    for (int i = 1; i <= n; i++){
        res[i] = 0;
        exist[i] = false;
        posElement[i] = 0;
    }

    init(itChecked, itDistinc, posIT, 1, n, 1);
    for (int i = 1; i <= m; i++){
        cin >> x >> y;
        exist[x] = true;
        update(itDistinc, posIT[i], 1);
        if (posElement[x]){
            update(itDistinc, posIT[posElement[x]], -1);
            if (get(itDistinc, 1, n, posElement[x] + 1, i - 1, 1) == y - 1){
                posElement[x] = i;
                continue;
            }
            else{
                cout << -1;
                return 0;
            }
        }

        int vt = find(itChecked, 1, n, 1, y);
        if (vt + get(itChecked, 1, n, vt + 1, n, 1) != y || res[vt]){
            cout << -1;
            return 0;
        }

        res[vt] = x;
        update(itChecked, posIT[vt], 1);
        posElement[x] = i;
    }
    int j = 1;
    for (int i = 1; i <= n; i++){
        if (res[i] == 0){
            while (exist[j++]);
            res[i] = j - 1;
        }
        cout << res[i] << " ";
    }

    delete[] itChecked;
    delete[] itDistinc;
    delete[] posIT;
    delete[] posElement;
    delete[] exist;
    delete[] res;
    return 0;
}

