inline ull rd()
{
	ull s=0;
	char c=getchar();
	while(c<'0'||c>'9')
		c=getchar();
	while(c>='0'&&c<='9')
		s*=10llu,s+=c-'0',c=getchar();
	return s;
}
inline void MergeSort(ull *a,ull l,ull r)
{
	if(l==r)
		return;
	ull m=(l+r)/2;
	MergeSort(a,l,m);
	MergeSort(a,m+1,r);
	ull i=l,j=m+1,k=l;
	while(i<=m&&j<=r)
		t[k++]=(a[i]>a[j])?a[j++]:a[i++];
	while(i<=m)
		t[k++]=a[i++];
	while(j<=r)
		t[k++]=a[j++];
	for(ull i=l;i<=r;++i)
		a[i]=t[i];
	return;
}
int main(void)
{
	scanf("%llu %llu",&n,&k);
	ull ans=0;
	for(register ull i=1;i<=n;++i)
		a[i]=rd();
	MergeSort(a,1,n);
	for(register ull i=1;i<=n;++i)
		for(register ull j=i+1;j<=n;++j)
		{
			if(a[j]<=a[i]+k&&a[j]>a[i])
			{
				++ans;
				break;
			}
		}
	printf("%llu",n-ans);
	return 0;
}

